mapper.update:
UPDATE shop_user
SET VERIFY = 'USER'
WHERE ID = #{id}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT * FROM shop_user WHERE 1=1
AND (id LIKE '%' || #{search, jdbcType=VARCHAR} || '%' OR name LIKE '%' || #{search, jdbcType=VARCHAR})
ORDER BY id ) A WHERE ROWNUM <= #{end} ) WHERE RNUM >= #{start}

