AND (id LIKE '%' || #{search, jdbcType=VARCHAR} || '%' OR name LIKE '%' || #{search, jdbcType=VARCHAR})
ORDER BY id ) A WHERE ROWNUM <= #{end} ) WHERE RNUM >= #{start}

